import React, {Component} from 'react';
import axios from 'axios';

export default class register extends Component {
    constructor(props) {
        super(props);
        this.state = {
            email: "",
            username: "",
            password: ""
        }
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeEmail = this.onChangeEmail.bind(this)
        this.onChangeUsername = this.onChangeUsername.bind(this)
        this.onChangePassword = this.onChangePassword.bind(this)

    };


    onSubmit(e) {
        e.preventDefault()
        const newUser = {
            email: this.state.email,
            username: this.state.username,
            password: this.state.password
        }

        axios.post("http://localhost:5000/register", {newUser}).then((res) => {
            alert(res.data.message)
        }).catch((err) => {
            alert("Error")
        })
        this.setState({
            email: "",
            username: "",
            password: ""
        })
    }

    onChangeEmail(e) {
        this.setState({
            email: e.target.value
        })
    }

    onChangeUsername(e) {
        this.setState({
            username: e.target.value
        })
    }

    onChangePassword(e) {
        this.setState({
            password: e.target.value
        })
    }

    render() {
        return (
            <form onSubmit={this.onSubmit} style={{
                height: "300px",
                width: "600px",
                margin: "auto"
            }}>
                <div className="container" style={{
                    position: "relative",
                    height: "200px",
                    width: "500px",
                    textAlign: "left"
                }}>
                    <h2>Register:</h2>
                    <div className="email"> 
                        <label>Email:</label>                       
                        <input type="text" placeholder="Enter Email" value={this.state.email} onChange={this.onChangeEmail} autoComplete="off" style={{
                            width: "100%"
                        }}/>                      
                    </div>
                    <br />
                    <div className="username"> 
                        <label>Username:</label>                    
                        <input type="text" placeholder="Enter Username" value={this.state.username} onChange={this.onChangeUsername} autoComplete="off" style={{
                            width: "100%"
                        }}/>
                    </div>
                    <br />
                    <label>Password:</label>
                    <div className="password">
                        <input type="password" placeholder="Enter Password" value={this.state.password} onChange={this.onChangePassword} autoComplete="off" style={{
                            width: "100%"
                        }}/>
                    </div>
                    <div className="submit" style={{
                        position: "relative"
                    }}>
                        <button type="submit" onClick={this.onSubmit} style={{
                            marginTop: "30px",
                            float: "right",
                            display: "inline-block",
                            width: "100%"
                        }}>Submit</button>
                    </div>
                </div>
            </form>
        )
    }
}
