import React, {Component} from 'react';
import axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';



export default class usersList extends Component {

    constructor(props) {
        super(props);
        
        this.state = {
            users: []
        }
        this.deleteUser = this.deleteUser.bind(this)
        this.getUsers = this.getUsers.bind(this);
    };

    getUsers() {
        axios.get("http://localhost:5000/users").then((res) => {
            const users = res.data;
            this.setState({users})
        }).catch((err) => {
            alert("Error: " + err)
        })
    }

    componentDidMount() {
        this.getUsers()
    }

    componentDidUpdate() {
        this.getUsers()
    }

    deleteUser(user) {
        axios.post("http://localhost:5000/users/deleteUser", {user}).then((res) => {
            const updateUser = res.data;
            this.setState({users: updateUser});
            alert('User Deleted Successfully')
        }).catch((err) => {
            alert('Error: ' + err);
        })
    }
    
    render() {
        return (
            <table class="table" style={{position: 'absolute', paddingTop: '50px', left: '50px', backgroundColor: '#f2f2f2'}}>
                <thead>
                    <tr style={{fontWeight: 'bold'}}>
                        List of Users
                    </tr>
                </thead>
                <tbody>
                    {
                        this.state.users.map((user,i) => {
                            return (
                                <tr key={i}>
                                    <td>
                                        {user.username}
                                    </td>
                                    <td>
                                        <button type='submit' onClick={() => {this.deleteUser(user)}}>Delete</button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        )
    }
}
