import React, {Component} from 'react';
import axios from 'axios';

export default class login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: "",
            password: ""
        }
        this.onSubmit = this.onSubmit.bind(this);
        this.onChangeUsername = this.onChangeUsername.bind(this)
        this.onChangePassword = this.onChangePassword.bind(this)
        this.redirect = this.redirect.bind(this);
    };

    onChangeUsername(event) {
        this.setState({
            username: event.target.value
        })
    }

    onChangePassword(event) {
        this.setState({
            password: event.target.value
        })
    }

    onSubmit(event) {
        event.preventDefault()
        const credentials = {
            username: this.state.username,
            password: this.state.password
        }
        axios.post('http://localhost:5000/login', {credentials}).then((res) => {
            if(res.data.result === true) {
                
            }
        }).catch((err) => {
            alert('Error: ' + err)
        })
    }


    render() {
        return (
            <form onSubmit={this.onSubmit} style={{
                height: "300px",
                width: "600px",
                margin: "auto"
            }}>
                <div className="container" style={{
                    position: "relative",
                    height: "200px",
                    width: "500px",
                    textAlign: "left"
                }}>
                    <h2>Login:</h2>
                    <div className="username">                        
                        <input type="text" placeholder="Username" onChange={this.onChangeUsername} style={{
                            width: "100%"
                        }}/>                      
                    </div>
                    <br />
                    <div className="password">                      
                        <input type="password" placeholder="Password" onChange={this.onChangePassword} style={{
                            width: "100%"
                        }}/>
                    </div>
                    <div className="submit" style={{
                        position: "relative"
                    }}>
                        <button type="submit" onClick={this.onSubmit} style={{
                            marginTop: "30px",
                            float: "right",
                            display: "inline-block",
                            width: "100%"
                        }}>Submit</button>
                    </div>
                </div>
            </form>
        )
    }
}
